        this.getter = func
        this.value = this.get()
    }

    get() {
        Dep.target = this;
        this.getter() // 触发getter并且添加依赖，因为target已存在
        Dep.target = null
    }

    addDep(dep) {
        dep.addSub(this)
    }

    update() {
        this.getter() // 模拟视图更新
